import React, { Component } from 'react'
import {Link} from '../routes'
import slug from '../helper/slug'

 class podcastList extends Component {
  render() {
    const { podcasts } = this.props
    return (
      <div>
        { podcasts.map((clip) => (
        <Link prefetch 
        params={{
              slugChannel: slug(clip.channel.title),
            idChannel: podcasts.channel.id,
            slug: slug(podcasts.title),
            id: podcasts.id
    }}
    prefetch key={podcasts.id}>
          <a className='podcast'>
            <h3>{ clip.title }</h3>
            <div className='meta'>
              { Math.ceil(clip.duration / 60) } minutes
            </div>
          </a>
        </Link>
      ))}
      <style jsx>{`
        .podcast {
          display: block;
          text-decoration: none;
          color: #333;
          padding: 15px;
          border-bottom: 1px solid rgba(0,0,0,0.2);
          cursor: pointer;
        }
        .podcast:hover {
          color: #000;
        }
        .podcast h3 {
          margin: 0;
        }
        .podcast .meta {
          color: #666;
          margin-top: 0.5em;
          font-size: 0.8em;
        }
      `}</style>
      </div>
    )
  }
}
export default podcastList;
